INSERT INTO `wp1_posts` VALUES("126", "1", "2020-04-28 16:01:40", "2020-04-28 14:01:40", "<!-- wp:paragraph -->\n<p>Les&nbsp;<strong>trois anneaux des Elfes</strong>&nbsp;ou&nbsp;<strong>trois anneaux elfiques</strong>, aussi appelés&nbsp;<strong>les Trois</strong>&nbsp;sont, dans le&nbsp;<a href=\"https://fr.wikipedia.org/wiki/L%C3%A9gendaire_de_Tolkien\">légendaire</a>&nbsp;de l\'écrivain britannique&nbsp;<a href=\"https://fr.wikipedia.org/wiki/J._R._R._Tolkien\">J. R. R. Tolkien</a>&nbsp;les trois&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Anneaux_de_pouvoir\">anneaux de pouvoir</a>&nbsp;possédés par les&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Elfe_(Terre_du_Milieu)\">Elfes</a>&nbsp;de la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Terre_du_milieu\">Terre du milieu</a>. Ces anneaux sont&nbsp;:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong><a href=\"https://fr.wikipedia.org/wiki/Trois_anneaux_des_Elfes#Narya\">Narya</a></strong>, porté d\'abord par&nbsp;<a href=\"https://fr.wikipedia.org/wiki/C%C3%ADrdan\">Círdan</a>&nbsp;puis donné à&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Gandalf\">Gandalf</a>&nbsp;;</li><li><strong><a href=\"https://fr.wikipedia.org/wiki/Trois_anneaux_des_Elfes#Nenya\">Nenya</a></strong>, porté par&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Galadriel\">Galadriel</a>&nbsp;;</li><li><strong><a href=\"https://fr.wikipedia.org/wiki/Trois_anneaux_des_Elfes#Vilya\">Vilya</a></strong>, porté d\'abord par&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Gil-Galad\">Gil-Galad</a>&nbsp;puis donné à&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Elrond\">Elrond</a>.</li></ul>\n<!-- /wp:list -->", "Les anneaux des Elfes", "", "publish", "open", "open", "", "les-anneaux-des-elfes", "", "", "2020-04-28 16:01:40", "2020-04-28 14:01:40", "", "0", "http://projets/wordpress/?p=126", "0", "post", "", "0");
