INSERT INTO `wp1_posts` VALUES("234", "1", "2020-05-04 14:05:56", "2020-05-04 12:05:56", "<!-- wp:image {\"align\":\"center\",\"id\":23,\"width\":238,\"sizeSlug\":\"large\"} -->\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"http://projets/wordpress/wp-content/uploads/2020/04/aragorn-e1588254241677.jpg\" alt=\"\" class=\"wp-image-23\" width=\"238\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"dropCap\":true} -->\n<p class=\"has-drop-cap\"><strong><span class=\"has-inline-color has-luminous-vivid-orange-color\">A</span><a href=\"https://fr.wikipedia.org/wiki/D%C3%BAnedain_du_Nord#Les_Chefs_des_D%C3%BAnedain\">ragorn II</a>, fils d\'<a href=\"https://fr.wikipedia.org/wiki/Arathorn\">Arathorn</a>&nbsp;et de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Gilraen\">Gilraen</a>, petit-fils d\'<a href=\"https://fr.wikipedia.org/wiki/Arador\">Arador</a>, est d\'abord présenté dans le roman comme un «&nbsp;Rôdeur&nbsp;»  appelé  «&nbsp;Grands-Pas&nbsp;» &nbsp;;  on apprend par la suite qu\'il est le chef des&nbsp;<a href=\"https://fr.wikipedia.org/wiki/D%C3%BAnedain_du_Nord\">Dúnedain du Nord</a>. Il ignore ses origines jusqu\'à ses 20 ans, quand&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Elrond\">Elrond</a>&nbsp;de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Fendeval\">Fendeval</a>&nbsp;lui révèle sa véritable ascendance&nbsp;: il est le descendant direct de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Valandil\">Valandil</a>, le dernier fils d\'<a href=\"https://fr.wikipedia.org/wiki/Isildur\">Isildur</a>&nbsp;et petit-fils d\'<a href=\"https://fr.wikipedia.org/wiki/Elendil\">Elendil</a>. Aragorn est ainsi le dernier héritier encore en vie des trônes d\'<a href=\"https://fr.wikipedia.org/wiki/Arnor\">Arnor</a>&nbsp;et de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Gondor\">Gondor</a>, qu\'il restaurera après la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Guerre_de_l%27Anneau\">guerre de l\'Anneau</a>.</strong></p>\n<!-- /wp:paragraph -->", "Aragorn", "", "inherit", "closed", "closed", "", "7-revision-v1", "", "", "2020-05-04 14:05:56", "2020-05-04 12:05:56", "", "7", "http://projets/wordpress/2020/05/04/7-revision-v1/", "0", "revision", "", "0");
INSERT INTO `wp1_posts` VALUES("235", "1", "2020-05-04 14:07:09", "2020-05-04 12:07:09", "<!-- wp:image {\"align\":\"center\",\"id\":26,\"width\":238,\"sizeSlug\":\"full\"} -->\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><img src=\"http://projets/wordpress/wp-content/uploads/2020/04/frodon-e1588254284967.jpg\" alt=\"\" class=\"wp-image-26\" width=\"238\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong><span class=\"has-inline-color has-vivid-red-color\">Frodon Sacquet</span></strong> est le personnage principal de l\'œuvre&nbsp;<em><a href=\"https://fr.wikipedia.org/wiki/Le_Seigneur_des_anneaux\">Le Seigneur des anneaux</a></em>&nbsp;de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/J._R._R._Tolkien\">J. R. R. Tolkien</a>. C\'est un&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Hobbit\">hobbit</a>, qui porte l\'<a href=\"https://fr.wikipedia.org/wiki/Anneau_unique\">Anneau unique</a>&nbsp;jusqu\'au&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Mont_Destin\">Mont Destin</a>&nbsp;afin de le détruire.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->", "Frodon Sacquet", "", "inherit", "closed", "closed", "", "1-revision-v1", "", "", "2020-05-04 14:07:09", "2020-05-04 12:07:09", "", "1", "http://projets/wordpress/2020/05/04/1-revision-v1/", "0", "revision", "", "0");
INSERT INTO `wp1_posts` VALUES("236", "1", "2020-05-04 14:09:05", "2020-05-04 12:09:05", "<!-- wp:image {\"align\":\"center\",\"id\":171,\"width\":340,\"height\":150,\"sizeSlug\":\"large\"} -->\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large is-resized\"><img src=\"http://projets/wordpress/wp-content/uploads/2020/04/maxresdefault-1024x576.jpg\" alt=\"\" class=\"wp-image-171\" width=\"340\" height=\"150\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Fondé au milieu du&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Chronologie_d%27Arda#Le_Troisi%C3%A8me_%C3%82ge\">Troisième Âge</a>, la Comté est une région du Nord-Ouest de la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Terre_du_Milieu\">Terre du Milieu</a>, au cœur de l\'<a href=\"https://fr.wikipedia.org/wiki/Eriador\">Eriador</a>, un territoire correspondant à l\'ancien royaume d\'<a href=\"https://fr.wikipedia.org/wiki/Arnor\">Arnor</a>. Il est peuplé exclusivement de&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Hobbit\">Hobbits</a>, créatures apparentées aux&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Homme_(Terre_du_Milieu)\">Hommes</a>&nbsp;caractérisées par leur petite taille<sup><a href=\"https://fr.wikipedia.org/wiki/Comt%C3%A9_(Terre_du_Milieu)#cite_note-Prologue-1\">1</a></sup>. Du fait de la nature pacifique de ses habitants, l\'histoire de la Comté est presque dépourvue d\'événements marquants, à l\'exception des faits survenus durant la&nbsp;<a href=\"https://fr.wikipedia.org/wiki/Guerre_de_l%27Anneau\">guerre de l\'Anneau</a>.</p>\n<!-- /wp:paragraph -->", "La Conté", "", "inherit", "closed", "closed", "", "170-revision-v1", "", "", "2020-05-04 14:09:05", "2020-05-04 12:09:05", "", "170", "http://projets/wordpress/2020/05/04/170-revision-v1/", "0", "revision", "", "0");
